package pw.adithya.SPLT.adapters;import android.app.Activity;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.ArrayList;import co.dift.ui.SwipeToAction;import pw.adithya.SPLT.objects.Bill;import pw.adithya.SPLT.R;import pw.adithya.SPLT.objects.Participant;/** * Created by adithya on 17/11/17. */public class BillsAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder>{    Activity activity;    ArrayList<Bill> billsArrayList;    public class MyViewHolder extends SwipeToAction.ViewHolder<String> {        public TextView qtyTextView, descTextView, priceTextView, totalTextView;        public MyViewHolder(View view) {            super(view);            qtyTextView = view.findViewById(R.id.textview_bills_qty);            descTextView = view.findViewById(R.id.textview_bills_desc);            priceTextView = view.findViewById(R.id.textview_bills_price);            totalTextView = view.findViewById(R.id.textview_bills_total);        }    }    public BillsAdapter(ArrayList billsArrayList, Activity activity) {        this.billsArrayList = billsArrayList;        this.activity = activity;    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext())                .inflate(R.layout.child_bills, parent, false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(RecyclerView.ViewHolder holder, int position) {        MyViewHolder vh = (MyViewHolder) holder;        vh.qtyTextView.setText(String.valueOf(billsArrayList.get(position).qty));        vh.descTextView.setText(billsArrayList.get(position).desc);        vh.priceTextView.setText(String.format("%.2f", billsArrayList.get(position).price));        vh.totalTextView.setText(String.format("%.2f", billsArrayList.get(position).total));        vh.data = billsArrayList.get(position).desc;        if (billsArrayList.size() == 0)            activity.findViewById(R.id.cardview_headers).setVisibility(View.GONE);        else            activity.findViewById(R.id.cardview_headers).setVisibility(View.VISIBLE);    }    @Override    public int getItemCount() {        return billsArrayList.size();    }    public void update(ArrayList<Bill> data) {        billsArrayList.clear();        billsArrayList.addAll(data);        notifyDataSetChanged();    }}