package pw.adithya.SPLT.adapters;import android.app.Activity;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.ArrayList;import co.dift.ui.SwipeToAction;import pw.adithya.SPLT.objects.Participant;import pw.adithya.SPLT.R;/** * Created by adithya on 17/11/17. */public class ContributionsAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder>{    Activity activity;    ArrayList<Participant> participantsArrayList;    public class MyViewHolder extends SwipeToAction.ViewHolder<String> {        public TextView priceTextView, nameTextView;        public MyViewHolder(View view) {            super(view);            nameTextView = view.findViewById(R.id.textview_contributions_name);            priceTextView = view.findViewById(R.id.textview_contributions_money);        }    }    public ContributionsAdapter(ArrayList participantsArrayList, Activity activity) {        this.participantsArrayList = participantsArrayList;        this.activity = activity;    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext())                .inflate(R.layout.child_contributions, parent, false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(RecyclerView.ViewHolder holder, int position) {        MyViewHolder vh = (MyViewHolder) holder;        vh.nameTextView.setText(String.valueOf(participantsArrayList.get(position).name));        vh.priceTextView.setText(String.valueOf(participantsArrayList.get(position).contrib));    }    @Override    public int getItemCount() {        return participantsArrayList.size();    }}