package pw.adithya.SPLT.adapters;import android.app.Activity;import android.support.v7.widget.CardView;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.ArrayList;import pw.adithya.SPLT.R;import pw.adithya.SPLT.objects.Combined;/** * Created by adithya on 17/11/17. */public class MainAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder>{    Activity activity;    ArrayList<Combined> combinedArrayList;    public class MyViewHolder extends RecyclerView.ViewHolder {        public TextView titleTextView, dateTextView;        public RecyclerView recyclerView;        public CardView mainCardView;        public int[] colorArray = {R.color.green_sea, R.color.nephritis, R.color.belize_hole, R.color.wisteria, R.color.midnight_blue, R.color.orange, R.color.pumpkin, R.color.pomogrenate, R.color.silver, R.color.asbestos};        public MyViewHolder(View view) {            super(view);            titleTextView = view.findViewById(R.id.textview_title);            dateTextView = view.findViewById(R.id.textview_date);            recyclerView = view.findViewById(R.id.recyclerview_main_child);            mainCardView = view.findViewById(R.id.cardview_main);        }    }    public MainAdapter(ArrayList combinedArrayList, Activity activity) {        this.combinedArrayList = combinedArrayList;        this.activity = activity;    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext())                .inflate(R.layout.child_main, parent, false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(RecyclerView.ViewHolder holder, int position) {        MyViewHolder vh = (MyViewHolder) holder;        LinearLayoutManager layoutManager = new LinearLayoutManager(activity);        MainSummaryAdapter mainSummaryAdapter = new MainSummaryAdapter(combinedArrayList.get(position).participantsArraylist, activity, combinedArrayList.get(position).extras);        vh.recyclerView.setLayoutManager(layoutManager);        vh.recyclerView.setHasFixedSize(true);        vh.recyclerView.setAdapter(mainSummaryAdapter);        vh.titleTextView.setText(combinedArrayList.get(position).title);        vh.dateTextView.setText(combinedArrayList.get(position).date);        int colorInt = position;        if (colorInt >= vh.colorArray.length)            colorInt -= vh.colorArray.length;        vh.mainCardView.setCardBackgroundColor(activity.getResources().getColor(vh.colorArray[colorInt]));        Log.e("Position", position + "");        Log.e("ColorInt", colorInt + "");    }    @Override    public int getItemCount() {        if (combinedArrayList == null)            return 0;        else            return combinedArrayList.size();    }}