package pw.adithya.SPLT.adapters;import android.app.Activity;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.ArrayList;import co.dift.ui.SwipeToAction;import pw.adithya.SPLT.R;import pw.adithya.SPLT.objects.Participant;/** * Created by adithya on 17/11/17. */public class MainSummaryAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder>{    Activity activity;    ArrayList<Participant> participantsArrayList;    double extras;    public class MyViewHolder extends SwipeToAction.ViewHolder<String> {        public TextView nameTextView, totalTextView, statusTextView;        public MyViewHolder(View view) {            super(view);            nameTextView = view.findViewById(R.id.textview_summary_name);            totalTextView = view.findViewById(R.id.textview_summary_total);            statusTextView = view.findViewById(R.id.textview_summary_status);        }    }    public MainSummaryAdapter(ArrayList participantsArrayList, Activity activity, double extras) {        this.participantsArrayList = participantsArrayList;        this.activity = activity;        this.extras = extras;    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext())                .inflate(R.layout.child_main_summary, parent, false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(RecyclerView.ViewHolder holder, int position) {        Participant p = participantsArrayList.get(position);        MyViewHolder vh = (MyViewHolder) holder;        vh.nameTextView.setText(p.name);        extras = extras / 100 + 1;        double total = p.price * extras - p.contrib;        if (total < 0.0)        {            vh.statusTextView.setText("paid");            total = -total;        }        else        {            vh.statusTextView.setText("owes");        }        vh.totalTextView.setText(String.format("%.2f", total));    }    @Override    public int getItemCount() {        if (participantsArrayList == null)            return 0;        else            return participantsArrayList.size();    }}