package pw.adithya.SPLT.adapters;import android.app.Activity;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.ArrayList;import co.dift.ui.SwipeToAction;import pw.adithya.SPLT.objects.Extra;import pw.adithya.SPLT.objects.Participant;import pw.adithya.splitandpay.R;/** * Created by adithya on 17/11/17. */public class SummaryAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder>{    Activity activity;    ArrayList<Participant> participantsArrayList;    double extras;    public class MyViewHolder extends SwipeToAction.ViewHolder<String> {        public TextView nameTextView, totalTextView;        public MyViewHolder(View view) {            super(view);            nameTextView = view.findViewById(R.id.textview_summary_name);            totalTextView = view.findViewById(R.id.textview_summary_total);        }    }    public SummaryAdapter(ArrayList participantsArrayList, Activity activity, double extras) {        this.participantsArrayList = participantsArrayList;        this.activity = activity;        this.extras = extras;    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext())                .inflate(R.layout.child_summary, parent, false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(RecyclerView.ViewHolder holder, int position) {        Participant p = participantsArrayList.get(position);        MyViewHolder vh = (MyViewHolder) holder;        vh.nameTextView.setText(p.name);        vh.totalTextView.setText(String.format("%.2f", p.price * extras - p.contrib));    }    @Override    public int getItemCount() {        return participantsArrayList.size();    }}